from lxml import etree
#import requests
import time
from selenium import webdriver


def read_page(html):
    selector = etree.HTML(html)
    # 使用绝对路径定位a标签的title
    #con = selector.xpath('/html/body/div/a/@title')
    #nodes = selector.xpath('//div[contains(@class,"ant-col-20")]')
    
    data=''
    
    names = selector.xpath('//div[contains(@class,"ant-col-20")]/div[contains(@class,"ant-col-24")]/a/span')
    bands = selector.xpath('//div[contains(@class,"ant-col-24")]/div[contains(@class,"ant-col-7")]/div[contains(@class,"ant-row-flex ant-row-flex-top")]/div[contains(@class,"ant-col-24")]/p/span')
    for i in range(len(names)):
        data+=names[i].text+'\t'
        for j in range(i*5,(i+1)*5):
            data+=bands[j].text+'\t'
        data+='\n'
    print(data)
    
    return data

def get_html(page,n):

    driver = webdriver.Chrome()
    driver.maximize_window()
    driver.implicitly_wait(6)
    driver.get("https://www.reagent.com.cn/")
    time.sleep(1)
    driver.find_element_by_link_text(page).click()
    time.sleep(1)
    #url=driver.current_url# current_url 方法可以得到当前页面的URL
    #print(url)
    html=driver.page_source
    #print(html)
    for page_num in range(2,n):
        driver.find_element_by_link_text(str(4)).click()
        html+='\n'+driver.page_source
    driver.quit()
    return html



